// title: simple marker customizations: color
import {Marker} from './lib/marker';

export default (map: google.maps.Map) => {
  const m1 = new Marker({map, position: {lat: 50, lng: 10}});
  const m2 = new Marker({map, position: {lat: 50, lng: 11}});
  const m3 = new Marker({map, position: {lat: 51, lng: 10}});
  const m4 = new Marker({map, position: {lat: 51, lng: 11}});
  const m5 = new Marker({map, position: {lat: 50.5, lng: 10.5}});

  // the new color-attribute will update all color-properties of
  // the markers with matching colors (all of these attributes can also be
  // specified with the marker-options in the constructor)
  m1.color = '#4285F4';
  m2.color = '#DB4437';
  m3.color = '#F4B400';
  m4.color = '#0F9D58';

  // alternatively, you can specify your own values for each of the
  // properties, like so:
  m5.backgroundColor = 'white';
  m5.borderColor = 'black';
  m5.glyphColor = 'salmon';

  map.fitBounds(
    new google.maps.LatLngBounds({lat: 49.5, lng: 9.5}, {lat: 51.5, lng: 11.5})
  );
};

04.pin-icons.ts.source:
// title: simple marker customizations: icons
import {Marker} from './lib/marker';
import {MaterialIcons, MaterialIconsStyle} from './lib/icons';

export default (map: google.maps.Map) => {
  // first we need to register the icon-provider, which is a function that knows how to create the kind of dom-element needed for an icon-set
  Marker.registerIconProvider(
    MaterialIcons({style: MaterialIconsStyle.FILLED})
  );

  const m1 = new Marker({
    map,
    position: {lat: 50, lng: 10},
    scale: 1.5,
    color: '#4285F4'
  });
  const m2 = new Marker({
    map,
    position: {lat: 50, lng: 11},
    scale: 1.5,
    color: '#DB4437'
  });
  const m3 = new Marker({
    map,
    position: {lat: 51, lng: 10},
    scale: 1.5,
    color: '#F4B400'
  });
  const m4 = new Marker({
    map,
    position: {lat: 51, lng: 11},
    scale: 1.5,
    color: '#0F9D58'
  });

  m1.icon = 'restaurant';
  m2.icon = 'nightlife';
  m3.icon = 'church';
  m4.icon = 'home';

  map.fitBounds(
    new google.maps.LatLngBounds({lat: 49.5, lng: 9.5}, {lat: 51.5, lng: 11.5})
  );
};
